import React from "react";
import Header from './components/Header';
import Users from "./components/Users";
import AddUser from "./components/AddUser";

class App extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      users: [
        {
          id: 1,
          name: 'Фредди Меркьюри',
          description: 'Легендарный британский певец, композитор и фронтмен рок-группы Queen.',
          isHappy: true
        },
        {
          id: 2,
          name: 'Gene Simmons',
          description: 'Бас-гитарист, вокалист и один из основателей группы KISS, известный своим сценическим образом "The Demon".',
          isHappy: false
        },
      ]
    };
    this.addUser = this.addUser.bind(this);
  }

  render() {
    return (
      <div>
        <Header text="Artists" />
        <main>
          <Users users={this.state.users} />
        </main>
        <aside>
          <AddUser onAdd={this.addUser} />
        </aside>
      </div>
    );
  }

  addUser(user) {
    const id = this.state.users.length + 1;
    this.setState(
      { users: [...this.state.users, { id, ...user }] },
      () => console.log(this.state.users) // Теперь лог выведет обновленный state
    );
  }
}

export default App;
