import React from "react";
import ReactDOMClient from "react-dom/client"; // Обрати внимание на 'react-dom/client'

function App(props) { // props - объект
  const inputClick = () => {
    console.log("Clicked");
  };

  const mouseOver = () => console.log("Mouse over");

  return (
    <div>
      <h1>{props.text}</h1> {/* props.text вместо просто text */}
      <input placeholder="Hellooo)" onClick={inputClick} onMouseEnter={mouseOver} />
    </div>
  );
}

// Используем новый метод createRoot
const app = ReactDOMClient.createRoot(document.getElementById("app"));
app.render(<App text="Hello, React!" />); // Передаём text как пропс
