import React from "react"
import User from "./User"

class Users extends React.Component {
    state = {
        users : [
            {
                id: 1,
                Name: 'Фредди Меркьюри',
                Decription: 'Легендарный британский певец, композитор и фронтмен рок-группы Queen.',
                isHappy: true
            },
            {
                id: 2,
                Name: 'Gene Simmons',
                Decription: 'Бас-гитарист, вокалист и один из основателей группы KISS, известный своим сценическим образом "The Demon".',
                isHappy: false
            },  
        ]
    }

    render() {
        if (this.state.users.length > 0) {
            return (<div className="user-div">
                {this.state.users.map((el) => 
                    (<div>
                        <h2>{el.Name}</h2>
                        <p>{el.Decription}</p>
                        <p>{el.isHappy ? 'Суета' : 'Грусть'}</p>
                    </div>))
                }
                </div>)
        } else {
            return (<div className="user-div">Нифига нет</div>)
        }
    }
}

export default Users
