import React from "react";

class AddUser extends React.Component {
    state = {
        name: "",
        description: "",  // Исправлено (было discription)
        isHappy: false,
    };

    handleSubmit = (e) => {
        e.preventDefault(); // предотвращаем перезагрузку страницы
        if (!this.state.name.trim() || !this.state.description.trim()) return; // проверка на пустые поля

        this.props.onAdd({
            name: this.state.name,
            description: this.state.description,  // Исправлено
            isHappy: this.state.isHappy,
        });

        // Очистка полей после добавления
        this.setState({
            name: "",
            description: "",
            isHappy: false
        });
    };

    render() {
        return (
            <form onSubmit={this.handleSubmit}>
                <input
                    type="text"
                    placeholder="Полное имя"
                    value={this.state.name} // Добавлено value
                    onChange={(e) => this.setState({ name: e.target.value })}
                />
                <textarea
                    placeholder="Описание"
                    value={this.state.description} // Добавлено value
                    onChange={(e) => this.setState({ description: e.target.value })}
                />
                <label htmlFor="isHappy">На суете?</label>
                <input
                    type="checkbox"
                    id="isHappy"
                    checked={this.state.isHappy} // Добавлено checked
                    onChange={(e) => this.setState({ isHappy: e.target.checked })}
                />
                <button type="submit">Добавить</button> {/* type="submit" вместо button */}
            </form>
        );
    }
}

export default AddUser;
