import React from "react";
import Header from './components/Header'
import Users from "./components/Users";
import AddUser from "./components/AddUser";

class App extends React.Component {
  state = {
    users : [
        {
            id: 1,
            Name: 'Фредди Меркьюри',
            Decription: 'Легендарный британский певец, композитор и фронтмен рок-группы Queen.',
            isHappy: true
        },
        {
            id: 2,
            Name: 'Gene Simmons',
            Decription: 'Бас-гитарист, вокалист и один из основателей группы KISS, известный своим сценическим образом "The Demon".',
            isHappy: false
        },  
    ]
  } 
  render(){
    return (
    <div>
        <Header text="Artists"/>
        <main>
          <Users users={this.state.users}/>
        </main>
        <aside>
          <AddUser users={this.state.users}/>
        </aside>
    </div>
    )
  }
}

export default App
