import React from "react";
import ReactDOMClient from "react-dom/client"; // Обрати внимание на 'react-dom/client'

class App extends React.Component {
  inputClick() {console.log("Clicked")};

  mouseOver() {console.log("Mouse over")}

  render(){
    <div>
      <h1>{this.props.text}</h1> {/* props.text вместо просто text */}
      <input placeholder="Hellooo)" onClick={this.inputClick} onMouseEnter={this.mouseOver} />
    </div>
  }
}

// Используем новый метод createRoot
const app = ReactDOMClient.createRoot(document.getElementById("app"));
app.render(<App text="Hello, React!" />); // Передаём text как пропс
