import React from "react";

class AddUser extends React.Component {
    state = {
        name: "",
        discription: "",
        isHappy: false,
    }
    render() {
        return (
            <form>
                <input placeholder="Полное имя" onChange={(e) => this.setState({name: e.target.value})}/>
                <textarea placeholder="Описание" onChange={(e) => this.setState({discription: e.target.value})}/>
                <label htmlFor="isHappy">На суете?</label>
                <input type="checkbox" id="isHappy" onChange={(e) => this.setState({isHappy: e.target.checked})}/>
                <button type="button">Добавить</button>
            </form>
        )
    }
}

export default AddUser
